INSERT INTO `catalog_product_entity_text` VALUES (104,75,0,36,'MÔ TẢ SẢN PHẨM\r\nDầu gội Genesis Bain Hydra-Fortifiant được thiết kế dành cho tóc yếu, làm khoẻ sợi tóc và đánh thức các nang tóc “đang ngủ đông” để cải thiện kết cấu và khả năng phục hồi cho tóc.\r\n\r\n- Nhẹ nhàng loại bỏ bã nhờn & các hạt ô nhiễm từ da đầu và tóc.\r\n\r\n- Kiểm soát dầu, ngăn ngừa nguy cơ rụng tóc.\r\n\r\n- Tính năng chống gãy và tăng cường chất xơ.\r\n\r\n- Cung cấp sức mạnh và sự mềm mại nhanh chóng.\r\n\r\n- Cung cấp độ ẩm và tỏa sáng.\r\n\r\nĐược Glamour trao tặng giải Dầu gội chuyên nghiệp tốt nhất cho mái tóc hư tổn năm 2020.\r\n\r\nKhông chứa Silicone, Sunfat\r\n\r\nSản xuất tại Tây Ban Nha'),(105,76,0,36,'Dầu gội phục hồi dành cho tóc thường đến dầu, tóc yếu, dễ bị rụng do gãy rụng'),(106,85,0,36,'KERASTASE Genesis Bain Hydra-Fortifiant - Dầu gội chống rụng tóc'),(107,75,0,37,'MÔ TẢ SẢN PHẨM\r\nDầu gội Elixir Ultime Le Bain được chiết xuất từ các loại dầu dưỡng nổi tiếng trong việc làm đẹp giúp cung cấp độ bóng và nuôi dưỡng tóc tối ưu.\r\n\r\nThúc đẩy mái tóc bóng mượt lâu dài. Cung cấp dinh dưỡng cần thiết cho mái tóc khỏe mạnh.\r\n\r\n- Dầu Marula: Chứa nhiều chất chống oxy hóa, axit béo thiết yếu và axit amin, có hiệu quả trong việc giữ cho tóc mềm mại, dẻo dai và giữ ẩm.\r\n\r\n- Dầu hoa trà: Một chất làm mềm tuyệt vời để giữ cho tóc ẩm và mềm mại, nó hấp thụ rất nhanh để lại mái tóc cực kỳ bóng mượt.\r\n\r\n- Dầu Argan: Với hàm lượng cao chất chống oxy hóa, axit béo thiết yếu và vitamin E, giúp tăng độ đàn hồi, hydrat hóa, làm mềm và phục hồi tóc bóng mượt một cách tự nhiên.\r\n\r\nSản xuất tại Tây Ban Nha'),(108,76,0,37,'Dầu gội đa năng cung cấp độ bóng và nuôi dưỡng tóc tối ưu giúp mái tóc bóng mượt mềm mại và phảng phất mùi hương tinh tế quyến rũ sang trọng'),(109,85,0,37,'KERASTASE Elixir Ultime Le Bain - Dầu gội cho tóc hư tổn & bóng mượt tóc'),(110,75,0,38,'MÔ TẢ SẢN PHẨM\r\nTấy tế bào chết cho da đầu này có thể thay thế dầu gội truyền thống và làm sạch sâu da đầu và tóc. Kết cấu thanh lọc của nó được thiết kế đặc biệt cho da đầu và tóc dễ bị nhờn, để chống lại dầu và gàu.\r\n\r\n- Khả năng thải độc của muối biển giúp làm sạch sâu da đầu, loại bỏ hiệu quả các tạp chất, bụi bẩn và tích tụ sản phẩm, giúp tóc khỏe hơn và đẹp hơn.\r\n\r\n- Vitamin B6 và Axit Salicylic bảo vệ da đầu với lợi ích chống viêm.\r\n\r\n- Loại bỏ bã nhờn sâu, bụi ô nhiễm, tích tụ sản phẩm và tạp chất (gấp 3 lần bụi ô nhiễm so với dầu gội truyền thống).\r\n\r\n- Nâng cao hiệu quả của các sản phẩm được sử dụng sau đó.\r\n\r\n- Kích thích tuần hoàn máu trên đầu thông qua việc massage.\r\n\r\n- Ngay lập nâng cao chân tóc và giữ cho mái tóc bồng bềnh lâu dài (72% sau khi sử dụng).\r\n\r\n- Thải độc da đầu & tóc ngay lập tức.\r\n\r\n- Thúc đẩy tái tạo da và làm thông thoáng lỗ chân lông cho sức khỏe da đầu tối ưu.\r\n\r\nSản xuất tại Tây Ban Nha'),(111,76,0,38,'Tẩy da chết da đầu chiết xuất từ muối biển tràn đầy năng lượng giúp làm sạch sâu da đầu đồng thời loại bỏ các tạp chất tăng sâu và mang lại sự tươi mát lâu dài. Dành cho da dầu, tóc nhanh bết và nhiều gàu.'),(112,85,0,38,'KERASTASE Scrub Energisant - Tẩy da chết da dầu, tóc bết & gàu'),(113,75,0,39,'MÔ TẢ SẢN PHẨM\r\nTấy tế bào chết cho da đầu này có thể thay thế dầu gội truyền thống và làm sạch sâu da đầu và tóc. Kết cấu thanh lọc của nó được thiết kế cho mọi loại da.\r\n\r\n- Chiết xuất cam giúp làm sạch da đầu, loại bỏ bụi bẩn và da chết, kết hợp cùng dầu jojoba giúp làm mềm và giảm kích ứng.\r\n\r\n- Thành phần rau má giúp kháng khuẩn, làm sạch da đầu mà không làm kích ứng da.\r\n\r\n- Vitamin B5 là thành phần thiết yếu của tóc có khả năng điều trị và ngăn chặn rụng tóc cực kỳ hiệu quả. Các tác động từ bên ngoài như uốn, duỗi, nhuộm làm “khô cạn” dưỡng chất khiến tóc gãy rụng, lúc này Vitamin B5 sẽ bổ sung dinh dưỡng cho tóc.\r\n\r\n- Loại bỏ bã nhờn sâu, bụi ô nhiễm, tích tụ sản phẩm và tạp chất (gấp 3 lần bụi ô nhiễm so với dầu gội truyền thống).\r\n\r\n- Nâng cao hiệu quả của các sản phẩm được sử dụng sau đó.\r\n\r\n- Kích thích tuần hoàn máu trên đầu thông qua việc massage.\r\n\r\nDành cho mọi loại da kể cả da nhạy cảm, da đầu bị bong tróc và ngứa ngáy khó chịu.\r\n\r\nSản xuất tại Tây Ban Nha'),(114,76,0,39,'Tẩy da chết da đầu chiết xuất từ cam giúp kích thích quá trình tái tạo và phục hồi tóc, để tóc thật sự chắc khoẻ, giảm gãy rụng, ít tiết dầu, tạm biệt tình trạng tóc bết'),(115,85,0,39,'KERASTASE Scrub Apaisant - Tẩy da chết mọi loại da'),(116,75,0,40,'MÔ TẢ SẢN PHẨM\r\nChứa nhiều thành phần thiên nhiên có tác dụng làm trắng da mà không gây tổn hại cho làn da, làm tan hắc tố da nhanh, mang lại làn da trắng hồng tự nhiên và tái tạo mô tế bào\r\n\r\n- Tẩy sạch lớp sừng chết, hắc tố melanin - nguyên nhân chính gây nên hiện tượng nám, tàn nhang, đốm nâu và sạm da.\r\n\r\n- Chiết xuất Cam Chanh giàu vitamin C và khoáng chất giúp nuôi dưỡng da trắng hồng và rạng rỡ tự nhiên, chống lão hoá.\r\n\r\n- Nhẹ nhàng làm mềm vùng da khô ở các vị trí như đầu gối, khuỷu tay, gót chân…\r\n\r\n- Hương thơm tươi mát dễ chịu.\r\n\r\n- Phù hợp cho cả làn da cháy nắng.\r\n\r\nSản xuất tại Nhật Bản'),(117,76,0,40,'Sữa tắm trắng da toàn thân số 1 bảng xếp hạng Cosme Nhật Bản với hương cam tự nhiên có tác dụng làm trắng và tẩy tế bào chết cho da, mang lại trải nghiệm thoải mái và dễ chịu.'),(118,85,0,40,'WHITE CONC Vitamin-C Body Shampoo - Sữa tắm trắng da'),(131,75,0,45,'MÔ TẢ SẢN PHẨM\r\nChiết xuất từ ​​trái cây tự nhiên giúp bổ sung AHA, loại bỏ lớp melanin cũ và bụi bẩn sâu bên trong lớp biểu bì, giúp da trắng, trong và mịn màng. Đồng thời, làm sạch tế bào giúp kích thích các tế bào da mới, làm giảm sự khô và thô ráp của da.\r\n\r\nChứa tới 9 thành phần làm sáng, nổi bật nhất là Arbutin giúp:\r\n\r\n- Giảm cháy nắng, sạm da.\r\n\r\n- Điều trị làm sáng da.\r\n\r\n- Ngăn ngừa bỏng nắng và hình thành các đốm nâu trên da.\r\n\r\n- Làm lành vết thương, nâng các tông màu da tối sau vài tháng sử dụng, giúp làm đều màu da.\r\n\r\nNgoài ra, sữa tắm này còn có Collagen và Hyaluronic Acid (HA) giúp bổ sung nước và tế bào, tăng độ đàn hồi cho da. Da sẽ sạch, mịn và giảm nếp nhăn.\r\n\r\nKhông khoáng, không cồn, không màu.\r\n\r\nDành cho mọi loại da.\r\n\r\nSản xuất tại Nhật Bản'),(132,76,0,45,'Sữa tắm giúp tẩy tế bào chết và làm trắng da toàn thân an toàn và hiệu quả. Công thức mới bổ sung nhiều độ ẩm cho da, mang lại một làn da trắng, hồng hào và mịn màng.'),(133,85,0,45,'MANIS White Body Shampoo Moisture - Sữa tắm dưỡng ẩm trắng da'),(134,75,0,46,'MÔ TẢ SẢN PHẨM\r\nChiết xuất từ ​​trái cây tự nhiên giúp bổ sung AHA, loại bỏ lớp melanin cũ và bụi bẩn sâu bên trong lớp biểu bì, giúp da trắng, trong và mịn màng. Đồng thời, làm sạch tế bào giúp kích thích các tế bào da mới, làm giảm sự khô và thô ráp của da.\r\n\r\nChứa tới 9 thành phần làm sáng, nổi bật nhất là Arbutin giúp:\r\n\r\n- Giảm cháy nắng, sạm da.\r\n\r\n- Điều trị làm sáng da.\r\n\r\n- Ngăn ngừa bỏng nắng và hình thành các đốm nâu trên da.\r\n\r\n- Làm lành vết thương, nâng các tông màu da tối sau vài tháng sử dụng, giúp làm đều màu da.\r\n\r\nNgoài ra, sữa tắm này còn có Collagen và Hyaluronic Acid (HA) giúp bổ sung nước và tế bào, tăng độ đàn hồi cho da. Da sẽ sạch, mịn và giảm nếp nhăn.\r\n\r\nKhông khoáng, không cồn, không màu.\r\n\r\nDành cho mọi loại da.\r\n\r\nSản xuất tại Nhật Bản'),(135,76,0,46,'Sữa tắm giúp tẩy tế bào chết và làm trắng da toàn thân an toàn và hiệu quả. Công thức mới bổ sung nhiều độ ẩm cho da, mang lại một làn da trắng, hồng hào và mịn màng.'),(136,85,0,46,'MANIS White Body Shampoo Moisture - Sữa tắm dưỡng ẩm trắng da'),(137,75,0,47,'MÔ TẢ SẢN PHẨM\r\nChiết xuất từ ​​trái cây tự nhiên giúp bổ sung AHA, loại bỏ lớp melanin cũ và bụi bẩn sâu bên trong lớp biểu bì, giúp da trắng, trong và mịn màng. Đồng thời, làm sạch tế bào giúp kích thích các tế bào da mới, làm giảm sự khô và thô ráp của da.\r\n\r\nChứa tới 9 thành phần làm sáng, nổi bật nhất là Arbutin giúp:\r\n\r\n- Giảm cháy nắng, sạm da.\r\n\r\n- Điều trị làm sáng da.\r\n\r\n- Ngăn ngừa bỏng nắng và hình